package FrameTest2;
public class Emplyer {
	private  String name;
	private int gender;
	private String dept;
	
	public Emplyer(String name,int gender,String dept) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.gender = gender;
		this.dept = dept;
	}
	
	public int getGender() {
		return this.gender ;
	}

	public String getDept() {
		return dept;
	}

	public String getName() {
		return this.name;
	}
	

	
   
}
